from app import dbimport ossep=os.sepclass User(db.Model):    id = db.Column(db.Integer,primary_key=True)    username=db.Column(db.String(64),index=True,unique=True)    userpassword=db.Column(db.String(64))    posts=db.relationship('Post',backref='author',lazy='dynamic')    def is_authenticated (self):        return True    def is_active(self):        return True    def is_anonymous(self):        return False    def get_id(self):        try:                    return unicode(self.id)        except NameError:            return str(self.id)    def __repr__(self):        return '<User %r>'%(self.username)class Post(db.Model):    id=db.Column(db.Integer,primary_key=True)    tilte=db.Column(db.String(200))    body=db.Column(db.String)    timestamp=db.Column(db.DateTime)    user_id=db.Column(db.Integer,db.ForeignKey('user.id'))    pinls=db.relationship('Pinl',backref='postt',lazy='dynamic')        def __repr__(self):        return '<Post %r>'%(self.body)class Pinl(db.Model):	id=db.Column(db.Integer,primary_key=True)	plbody=db.Column(db.String(200))	timestamp=db.Column(db.DateTime)	body_id=db.Column(db.Integer,db.ForeignKey('post.id'))	def __repr__(self):		return '<Pinl %r>'%(self.plbody)